import React,{useState,useEffect} from 'react'
import './App.css'
function Chat({name,roomId,socket}) {
const [currentMessage,setCurrentMessage] = useState("")
const [messageList,setMessageList] = useState([])
console.log(name)
console.log(roomId)
const sendMessage =async()=>{
  if(currentMessage!==""){
    const messageDtaa={
      author:name,
      room:roomId,
      message:currentMessage,
      time:new Date(Date.now()).getHours() + ":"+ new Date(Date.now()).getMinutes()
    }
await socket.emit("send_message",messageDtaa)
  }
}
useEffect(()=>{
socket.on("recieve_message",(data)=>{
  console.log(data)
setMessageList((list)=>[...list,data])
})
},[socket])
  return (
    <div>
      <h3>Live Chat</h3>
      <div className='chat_body'>
        {
          messageList.map((list)=>{
            return(
              <div style={{display:'flex',flexDirection:'row',width:'100%' , margin:'2rem'}}>
                <h2>{list.author}</h2>

                 <h2>{list.message}</h2>
                 <h4>{list.time}</h4>
              </div>

            )
          })
        }
        <div className='chat_content'>
        <input placeholder='enter a message' type='text' onChange={(e)=>setCurrentMessage(e.target.value)} style={{widows}}/>
        <button onClick={sendMessage}>Send</button>
        </div>

      </div>

    </div>
  )
}

export default Chat
